import io.appium.java_client.AppiumDriver;import io.appium.java_client.android.AndroidDriver;import org.junit.After;import org.junit.Before;import org.junit.Test;import org.openqa.selenium.remote.DesiredCapabilities;import java.net.URL;public class FirstTest {    private AppiumDriver driver;    @Before    public void setUp() throws Exception{        DesiredCapabilities capabilities = new DesiredCapabilities();        capabilities.setCapability("platformName","Android");        capabilities.setCapability("deviceName","Example");        capabilities.setCapability("platformVersion","10.0");        capabilities.setCapability("appium:automationName","uiautomator2");        capabilities.setCapability("appium:appPackage","org.wikipedia");        capabilities.setCapability("appium:appActivity","org.wikipedia.main.MainActivity");        capabilities.setCapability("app","C:\\JavaAppiumAutomation_Project\\JavaAppiumAutomation_Project\\apks\\org.wikipedia.apk");        driver = new AndroidDriver(new URL("http://127.0.0.1:4723/"), capabilities);    }    @After    public void tearDown(){        if (driver != null) {            driver.quit();        }        driver = null;    }    @Test    public void firstTest(){        System.out.println("first test run");    }}